package chap3;

public class Example305 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		System.out.println("i:"+i);
		System.out.println("++i:"+ ++i);
		System.out.println("i:"+i);
		System.out.println("i++:"+ i++);
		System.out.println("i:"+i);
		System.out.println("--i:"+ --i);
		System.out.println("i:"+i);
		System.out.println("i--:"+ i--);
		System.out.println("i:"+i);
	}
}/*Output
*i:1
*++i:2
*i:2
*i++:2
*i:3
*--i:2
*i:2
*i--:2
*i:1
*/
